import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import $ from 'jquery';
import { Redirect, withRouter } from 'react-router-dom';
import './Styles/Login.css';
import axios from 'axios';
import { getUser } from '../Actions/UserActions'
import { isLogged } from '../Actions/LoginActions'
import NavBar from './NavBar';

Window.$ = $;

class UserEditPage extends Component {
	constructor(props) {
		super(props);

		this.saveUser = this.saveUser.bind(this);
	}

	saveUser(e) {
		console.log(e);
	}

	componentWillMount() {
		this.props.getUser();
		this.props.isLogged(this.props.User.username);
	}

	render() {
		if (this.props.LoggedIn) {
			return (
				<div style={{ flex: '1 1 auto', flexFlow: 'column', minHeight: "70vh", marginTop: "2vh" }}>
					<form>
						<label>Username: <input type="text" name="username" /></label>
						<label>Email: <input type="email" name="email" /></label>
						<label>Password: <input type="password" name="password" /></label>
						<label>First Name: <input type="text" name="firstName" /></label>
						<label>Last Name: <input type="text" name="lastName" /></label>
						<label>Profile Picture Url: <input type="text" name="profilePic" /></label>
						{this.props.User.admin !== false && this.props.User.admin !== undefined ? <label><input type="checkbox" name="admin" value={true} />Is Admin </label> : ""}
						<button onClick={this.saveUser} className="btn btn-success">Save</button>
					</form>
				</div >
			)
		} else {
			return (
				<Redirect to="/login" />
			);
		}
	}
}

UserEditPage.propTypes = {
	isLogged: PropTypes.func.isRequired,
	getUser: PropTypes.func.isRequired,
	User: PropTypes.object
}

const mapStateToProps = (state) => ({
	LoggedIn: state.Login.User,
	User: state.Users.User,
	Error: state.Users.Error
})
export default withRouter(connect(mapStateToProps, { getUser, isLogged })(UserEditPage));
