import React, { Component } from 'react'
import ReactDOM from 'react-dom'
import { connect } from 'react-redux'
import { withRouter } from 'react-router-dom'
import PropTypes from 'prop-types';
import $ from 'jquery';
import axios from 'axios';
import './Styles/FileUploader.css';
import FileIcon, { defaultStyles } from 'react-file-icon';
import { listFiles } from '../Actions/FileActions'
import FileUploader from './FileUploader';
import { saveAs } from 'file-saver';
Window.$ = $;

class FileList extends Component {
	constructor(props) {
		super(props);

		this.downloadFile = this.downloadFile.bind(this);
	}

	downloadFile(e) {

		if (e.persist) {
			e.persist();
		}

		if (e.persistData) {
			e.persistData();
		}

		var filename = ReactDOM.findDOMNode(e.currentTarget).children[1].innerHTML;

		axios.get('../../search/' + ReactDOM.findDOMNode(e.currentTarget).children[1].innerHTML).then(response => {
			var blob = new Blob([response]);

			if (typeof window.navigator.msSaveBlob !== 'undefined') {
				// IE workaround for "HTML7007: One or more blob URLs were revoked by closing the blob for which they were created. These URLs will no longer resolve as the data backing the URL has been freed."
				window.navigator.msSaveBlob(blob, filename);
			} else {
				var URL = window.URL || window.webkitURL;
				var downloadUrl = URL.createObjectURL(blob);

				if (filename) {
					// use HTML5 a[download] attribute to specify filename
					var a = document.createElement("a");
					// safari doesn't support this yet
					if (typeof a.download === 'undefined') {
						window.location = downloadUrl;
					} else {
						a.href = downloadUrl;
						a.download = filename;
						document.body.appendChild(a);
						a.click();
					}
				} else {
					window.location = downloadUrl;
				}

				setTimeout(function () { URL.revokeObjectURL(downloadUrl); }, 100); // cleanup
			}
		});
	}

	componentWillMount() {
		this.props.listFiles();
	}

	render() {
		var k = 0;
		var visibility = false;
		var arr = [];
		return (
			!visibility ?
				<div onDragStart={visibility = !visibility} onDragLeave={visibility = !visibility} style={{ flex: '1 1 auto', marginTop: "2vh", minHeight: "80vh", maxHeight: "-webkit-fill-available", flexFlow: "row", display: "flex" }} >
					{

						this.props.Files.forEach(element => {
							var ext = element.split(".")[element.split(".").length - 1].toString();
							arr.push(
								React.createElement('div', { onClick: this.downloadFile, id: "div" + k++, style: { WebkitFontSmoothing: 'antialiased', width: "fit-content", flexDirection: "column", height: "fit-content", display: "flex" }, key: "myrfvkdiv" + k },
									[
										React.createElement(FileIcon, { extension: ext, key: "myrfvk" + k, ...defaultStyles[ext], size: 64, marginLeft: "10px" }),
										React.createElement('p', { style: { display: "block", textOverflow: "ellipsis", textAlign: "center", whiteSpace: "nowrap", overflow: "hidden", width: "64px" }, key: "myrfvklabel" + k }, element)
									])
							)
						})
					}
					{arr}
				</div> : <div onDragStart={visibility = !visibility} onDragLeave={visibility = !visibility} style={{ flex: '1 1 auto', marginTop: "2vh", minHeight: "80vh", maxHeight: "-webkit-fill-available", flexFlow: "row", display: "flex" }} >
					<FileUploader />
				</div>
		)
	}
}

FileList.propTypes = {
	listFiles: PropTypes.func.isRequired,
	Files: PropTypes.array
}

const mapStateToProps = (state) => ({
	Files: state.Files.Files,
	Error: state.Files.Error
})

export default withRouter(connect(mapStateToProps, { listFiles })(FileList));
