import React, { Component } from 'react';
import axios from 'axios';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import {withRouter} from 'react-router-dom';
//import './Styles/Logout.css';

class Logout extends Component {
	constructor(props){
		super(props);
		this.state = {
			username:'',
			password:'',
			loggedIn:{
				success: false, 
				error:null
			}
		};
		
		this.handleLogout = this.handleLogout.bind(this);
	}
	

	handleLogout(event){
		event.preventDefault();
		axios.post('/logout', {
			username: this.state.username,
			password: this.state.password,
		},{
			port:3001
		}).then((res)=>this.props.handleLogout(res.data));
	}
	
	render() {
		return (
			<button onClick={this.handleLogout} className="btn btn-primary btn-block" type="submit"><i className="fal fa-power-off"></i></button>
		);
	}
}

Logout.propTypes = {
	logout: PropTypes.func.isRequired,
	User: PropTypes.object,
}

const mapStateToProps = (state) => ({
	User: state.Users.User,
	LoggedIn: state.Users.LoggedIn
})

export default withRouter(connect(mapStateToProps, null)(Logout));
