import { GETUSER, DELUSER } from './Types'
import axios from 'axios';

export const getUser = () => dispatch => {
    axios.get('/Profile')
        .then(user => {
            dispatch({
                type: GETUSER,
                payload: user.data
            })
        })
}

export const deleteUser = () => dispatch => {
    axios.delete('/Profile/del')
        .then(user => {
            dispatch({
                type: DELUSER,
                payload: user.data
            })
        })
}
