import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import $ from 'jquery';
import { Redirect, withRouter } from 'react-router-dom';
import './Styles/Login.css';
import axios from 'axios';
import { getUser } from '../Actions/UserActions'
import { isLogged } from '../Actions/LoginActions'
import NavBar from './NavBar';

Window.$ = $;

class UserEditPage extends Component {
	constructor(props) {
		super(props);

		this.preventSubmit = this.preventSubmit.bind(this);
		this.saveUser = this.saveUser.bind(this);
	}

	preventSubmit(e) {
		if (e.stopPropagation) {
			e.stopPropagation();
		}

		if (e.preventDefault) {
			e.preventDefault();
		}
	}

	saveUser(e) {
		console.log('Username: ' + ReactDOM.findDOMNode(e.currentTarget.parentNode.children[0].children[0].children[0].children[1]).nodeValue);
		console.log('Username: ' + e.currentTarget.parentNode.children[0].children[0].children[0].children[1].value);
		console.log('Email: ' + e.currentTarget.parentNode.children[0].children[0].children[1].children[1].value);
		console.log('Password: ' + e.currentTarget.parentNode.children[0].children[0].children[2].children[1].value);
		console.log('First Name: ' + e.currentTarget.parentNode.children[0].children[0].children[3].children[1].value);
		console.log('Last Name: ' + e.currentTarget.parentNode.children[0].children[0].children[4].children[1].value);
		console.log('Profile Picture Url: ' + e.currentTarget.parentNode.children[0].children[0].children[5].children[1].value);
		console.log('Is Admin: ' + e.currentTarget.parentNode.children[0].children[0].children[6].children[1].value);
	}

	componentWillMount() {
		this.props.getUser();
		this.props.isLogged(this.props.User.username);
	}

	render() {
		if (this.props.LoggedIn) {
			return (
				<div style={{ flex: '1 1 auto', flexFlow: 'column', minHeight: "70vh", marginTop: "2vh", alignSelf: "center" }}>
					<form target="self" onSubmit={this.preventSubmit}>
						<table>
							<tbody>
								<tr>
									<td>Username: </td>
									<td><input type="text" name="username" style={{ border: "1px black solid" }} /></td>
								</tr>
								<tr>
									<td>Email: </td>
									<td><input type="email" name="email" style={{ border: "1px black solid" }} /></td>
								</tr>
								<tr>
									<td>Password: </td>
									<td><input type="password" name="password" style={{ border: "1px black solid" }} /></td>
								</tr>
								<tr>
									<td>First Name: </td>
									<td><input type="text" name="firstName" style={{ border: "1px black solid" }} /></td>
								</tr>
								<tr>
									<td>Last Name: </td>
									<td><input type="text" name="lastName" style={{ border: "1px black solid" }} /></td>
								</tr>
								<tr>
									<td>Profile Picture Url: </td>
									<td><input type="text" name="profilePic" style={{ border: "1px black solid" }} /></td>
								</tr>
								{this.props.User.admin !== false && this.props.User.admin !== undefined ?
									<tr>
										<td>Is Admin: </td>
										<td><input type="checkbox" name="admin" value={true} /></td>
									</tr> : ""}
							</tbody>
						</table>
						<button onClick={this.saveUser} className="btn btn-success">Save</button>
					</form>
				</div >
			)
		} else {
			return (
				<Redirect to="/login" />
			);
		}
	}
}

UserEditPage.propTypes = {
	isLogged: PropTypes.func.isRequired,
	getUser: PropTypes.func.isRequired,
	User: PropTypes.object
}

const mapStateToProps = (state) => ({
	LoggedIn: state.Login.User,
	User: state.Users.User,
	Error: state.Users.Error
})
export default withRouter(connect(mapStateToProps, { getUser, isLogged })(UserEditPage));
