import React, { Component } from 'react'
import ReactDOM from 'react-dom'
import { connect } from 'react-redux'
import { withRouter } from 'react-router-dom'

class FileUploader extends Component {
    constructor(props) {
        super(props);

        this.handleFiles = this.handleFiles.bind(this);
        this.handleDrop = this.handleDrop.bind(this);
    }

    handleDrop() {
        ReactDOM.findDOMNode(this).submit();
    }

    handleFiles(event) {
        if (event.preventDefault) {
            event.preventDefault();
        }

    }

    render() {
        return (
            <section id="Home">
                <div style={{ backgroundColor: "rgba(73.4, 60, 190,1)", paddingTop: "4%" }}>     <div style={{ display: "flex", flexFlow: "collumn wrap", height: "auto", marginLeft: "auto", marginRight: "auto", alignItems: "center", flexWrap: "nowrap", justifyContent: "center" }}>
                    <div style={{ flex: "0 0 auto", marginLeft: "10%", alignItems: "center", alignSelf: "center", justifyContent: "space-evenly", color: "azure" }}>

                        <form encType="multipart/form-data" method="POST" action="https://clouddriveserver.azurewebsites.net/backend/file" >
                            <input type="file" name="Files" multiple onChange={this.handleDrop} />
                        </form>
                    </div>
                </div>
                </div>
            </section>
        )
    }
}

const mapStateToProps = (state) => ({
})

export default withRouter(connect(mapStateToProps, null)(FileUploader));
