import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import $ from 'jquery';
import { Redirect, withRouter } from 'react-router-dom';
import './Styles/Login.css';
import axios from 'axios';
import { getUser } from '../Actions/UserActions'
import { isLogged } from '../Actions/LoginActions'
import NavBar from './NavBar';

Window.$ = $;

class UserEditPage extends Component {
	constructor(props) {
		super(props);

		this.saveUser = this.saveUser.bind(this);
	}

	saveUser(e) {
		console.log(e);
	}

	componentWillMount() {
		this.props.getUser();
	}

	render() {
		return (
			<div style={{ flex: '1 1 auto', flexFlow: 'column', minHeight: "70vh", marginTop: "2vh" }}>
				<form>
					<p>Username: </p><input type="text" name="username" />
					<p>Email: </p><input type="email" name="email" />
					<p>Password: </p><input type="password" name="password" />
					<p>First Name: </p><input type="text" name="firstName" />
					<p>Last Name: </p><input type="text" name="lastName" />
					<p>Profile Picture Url: </p><input type="text" name="profilePic" />
					{this.props.User.admin !== false && this.props.User.admin !== undefined ? <input type="checkbox" name="admin" value={true}> Is Admin</input> : ""}
					<button onClick={this.saveUser} className="btn btn-success">Save</button>
				</form>
			</div >
		)
	}
}

UserEditPage.propTypes = {
	getUser: PropTypes.func.isRequired,
	User: PropTypes.object
}

const mapStateToProps = (state) => ({
	User: state.Users.User,
	Error: state.Users.Error
})
export default withRouter(connect(mapStateToProps, { getUser })(UserEditPage));
