import React, { Component } from 'react'
import ReactDOM from 'react-dom'
import { connect } from 'react-redux'
import { withRouter } from 'react-router-dom'
import PropTypes from 'prop-types';
import $ from 'jquery';
import axios from 'axios';
import './Styles/FileUploader.css';
import FileIcon, { defaultStyles } from 'react-file-icon';
import { listFiles } from '../Actions/FileActions'
import FileUploader from './FileUploader';
Window.$ = $;

class FileList extends Component {
	constructor(props) {
		super(props);


		this.onEnter = this.onEnter.bind(this);
		this.onExit = this.onExit.bind(this);
		this.downloadFile = this.downloadFile.bind(this);
	}

	downloadFile(e) {

		const link = document.createElement('a');
		link.href = '../../search/' + ReactDOM.findDOMNode(e.currentTarget).children[1].innerHTML;
		link.download = ReactDOM.findDOMNode(e.currentTarget).children[1].innerHTML;
		link.click();
		ReactDOM.findDOMNode(link).remove();
	}

	onEnter(e) {
		e.preventDefault();
		e.stopPropagation();
		this.hidden = true;
		this.forceUpdate();
	}

	onExit(e) {
		e.preventDefault();
		e.stopPropagation();
		this.hidden = false;
		this.forceUpdate();
	}

	componentWillMount() {
		this.props.listFiles();
	}

	render() {
		let hidden = true;
		var k = 0;
		var arr = [];
		return (
			<div>
				<div onDrag={this.onEnter} onDragEnter={this.onEnter} /*onDragOver={this.onExit} onDragLeave={this.onExit}*/ style={{ flex: '1 1 auto', marginTop: "2vh", minHeight: "80vh", maxHeight: "-webkit-fill-available", flexFlow: "row", display: this.hidden ? "none" : "flex" }} >
					{

						this.props.Files.forEach(element => {
							var ext = element.split(".")[element.split(".").length - 1].toString();
							arr.push(
								React.createElement('div', { onClick: this.downloadFile, id: "div" + k++, style: { WebkitFontSmoothing: 'antialiased', width: "fit-content", flexDirection: "column", height: "fit-content", display: "flex" }, key: "myrfvkdiv" + k },
									[
										React.createElement(FileIcon, { extension: ext, key: "myrfvk" + k, ...defaultStyles[ext], size: 64, marginLeft: "10px" }),
										React.createElement('p', { style: { display: "block", textOverflow: "ellipsis", textAlign: "center", whiteSpace: "nowrap", overflow: "hidden", width: "64px" }, key: "myrfvklabel" + k }, element)
									])
							)
						})
					}
					{arr}
				</div>
				<div /*onDragEnter={this.onEnter}*/ onDragLeave={this.onExit} onDrop={this.onEnter} style={{ flex: '1 1 auto', marginTop: "2vh", minHeight: "80vh", maxHeight: "-webkit-fill-available", flexFlow: "row", display: this.hidden ? "flex" : "none" }} >
					<FileUploader />
				</div>
			</div >
		)
	}
}

FileList.propTypes = {
	listFiles: PropTypes.func.isRequired,
	Files: PropTypes.array
}

const mapStateToProps = (state) => ({
	Files: state.Files.Files,
	Error: state.Files.Error
})

export default withRouter(connect(mapStateToProps, { listFiles })(FileList));
