import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import $ from 'jquery';
import { Redirect, withRouter } from 'react-router-dom';
import './Styles/Login.css';
import axios from 'axios';
import { getUser, deleteUser } from '../Actions/UserActions'
import NavBar from './NavBar';

Window.$ = $;

class UserPage extends Component {
	constructor(props) {
		super(props);
	}

	deleteThisUser() {
		this.props.deleteUser();
	}

	componentWillMount() {
		this.props.getUser();
	}

	render() {
		return (
			<div style={{ flex: '1 1 auto', flexFlow: 'column', minHeight: "70vh", marginTop: "2vh" }}>
				<center>
					{this.props.User.profilePic !== undefined ? <p>Profile Picture Url: {this.props.User.profilePic}</p> : <div style={{ borderRadius: "100%", border: "1px solid black", width: "fit-content", padding: "10px 20px 20px 20px" }}><i class="fas fa-user-secret fa-7x"></i></div>}
					<br />
					<p>Username: {this.props.User.username}</p>
					{this.props.User.email !== undefined ? <p>Email: {this.props.User.email}</p> : ""}
					{this.props.User.firstName !== undefined ? <p>First Name: {this.props.User.firstName}</p> : ""}
					{this.props.User.lastName !== undefined ? <p>Last Name: {this.props.User.lastName}</p> : ""}
					{this.props.User.admin !== false && this.props.User.isAdmin !== undefined ? <p>Is Admin: {this.props.User.admin}</p> : ""}
					<button onClick={this.deleteThisUser} >Delete</button>
				</center>
			</div >
		)
	}
}

UserPage.propTypes = {
	getUser: PropTypes.func.isRequired,
	User: PropTypes.object
}

const mapStateToProps = (state) => ({
	User: state.Users.User,
	Error: state.Users.Error
})
export default withRouter(connect(mapStateToProps, { getUser, deleteUser })(UserPage));
