import React, { Component } from 'react'
import ReactDOM from 'react-dom'
import { connect } from 'react-redux'
import { withRouter } from 'react-router-dom'

class FileUploader extends Component {
    constructor(props) {
        super(props);

        this.handleFiles = this.handleFiles.bind(this);
        this.handleDrop = this.handleDrop.bind(this);
    }

    handleDrop() {
        ReactDOM.findDOMNode(this).submit();
    }

    handleFiles(event) {
        if (event.preventDefault) {
            event.preventDefault();
        }

    }

    render() {
        return (
            <form action="https://clouddriveserver.azurewebsites.net/backend/file"
                style={{ position: "absolute", width: "100%", paddingTop: "2.6%" }}
                className="dropzone"
                id="my-awesome-dropzone">
                <div class="fallback">
                    <input name="Files" type="file" multiple onDrop={this.handleDrop} onDragOver={this.handleFiles} />
                </div>
            </form>
        )
    }
}

const mapStateToProps = (state) => ({
})

export default withRouter(connect(mapStateToProps, null)(FileUploader));
