import React, { Component } from 'react'
import ReactDOM from 'react-dom'
import { connect } from 'react-redux'
import { withRouter } from 'react-router-dom'
import PropTypes from 'prop-types';
import $ from 'jquery';
import axios from 'axios';
import './Styles/FileUploader.css';
import FileIcon, { defaultStyles } from 'react-file-icon';
import { listFiles } from '../Actions/FileActions'
Window.$ = $;

class FileList extends Component {
	constructor(props) {
		super(props);
	}

	componentWillMount() {
		this.props.listFiles();
	}

	render() {
		var k = 0;
		var arr = [];
		return (
			<div style={{ flex: '1 1 auto', flexFlow: 'column' }} >
				{this.props.Files.forEach(element => {
					arr.push(React.createElement(FileIcon, { extension: element.split(".")[element.split(".").length - 1], key: "myrfvk" + k++ }, defaultStyles[element.split(".")[element.split(".").length - 1].toString()]))
				})}
				{arr}
				<FileIcon extension="docx" {...defaultStyles.docx} />
			</div>
		)
	}
}

FileList.propTypes = {
	listFiles: PropTypes.func.isRequired,
	Files: PropTypes.array
}

const mapStateToProps = (state) => ({
	Files: state.Files.Files,
	Error: state.Files.Error
})

export default withRouter(connect(mapStateToProps, { listFiles })(FileList));
