import React, { Component } from 'react'
import ReactDOM from 'react-dom'
import { connect } from 'react-redux'
import { withRouter } from 'react-router-dom'
import axios from 'axios';

class FileUploader extends Component {
    constructor(props) {
        super(props);

        this.handleFiles = this.handleFiles.bind(this);
        this.handleDrop = this.handleDrop.bind(this);
    }

    handleDrop(event) {
        if (event.preventDefault) {
            event.preventDefault();
        }

        axios.post('http://localhost:3001/backend/file/upload', {
            method: 'post',
            data: event.dataTransfer.files,
            contentType: "multipart/form-data"
        });
    }

    handleFiles(event) {
        if (event.preventDefault) {
            event.preventDefault();
        }

    }

    render() {
        return (
            <div style={{ width: "100%", paddingTop: "2.6%", height: "100%", paddingBottom: "100%" }} onDrop={this.handleDrop} onDragOver={this.handleFiles}>
            </div>
        )
    }
}

const mapStateToProps = (state) => ({
})

export default withRouter(connect(mapStateToProps, null)(FileUploader));
