import {LOGIN, LOGOUT} from '../Actions/Types'

const initialState = {
    User: {},
    LoggedIn: false,
    Error: null
};

export const LoginReducer = (state = initialState, action) => {
  switch (action.type) {
    case LOGIN:
      action.callback()
      return { 
          ...state, 
          User: JSON.parse(action.payload).User,
          LoggedIn:(JSON.parse(JSON.parse(action.payload).Error).err===null)?true:false,
          Error: (JSON.parse(JSON.parse(action.payload).Error).err!==null)?JSON.parse(JSON.parse(action.payload).Error).err:null
      };
      case LOGOUT:
        return { 
            ...state, 
            User: null,
            LoggedIn: false,
            Error: null
        };
    default:
      return state;
  }
}
