import React, { Component } from 'react';
import axios from 'axios';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import $ from 'jquery';
import {login} from '../Actions/LoginActions';
import {Redirect,withRouter} from 'react-router-dom';
import './Styles/Login.css';
Window.$ = $;

class Profile extends Component {
	constructor(props){
		super(props);

		this.handleChange = this.handleChange.bind(this);
		this.handleSignUp = this.handleSignUp.bind(this);
		this.handleLogin = this.handleLogin.bind(this);
		this.clickEvt = this.clickEvt.bind(this);
	}
	
	componentDidMount() {
		$("#Login").modal({
			closable:false
		}).modal("show");
	}
	
	componentWillUnmount() {
		$('#Login').modal({
			closable:true
		}).modal('toggle');
	}
	
	handleChange(event){
		const value = event.target.value;
		const name = event.target.name;
		this.setState({
			[name]: value
		});
	}
	
	handleLogin(event){
		event.preventDefault();
		this.props.login(this.state.username,this.state.password);
	}	
	
	handleSignUp(event){
		event.preventDefault();
		axios.post('/signup', {
			username: this.state.username,
			password: this.state.password,
		},{
			port:3001
		}).then((res)=>this.props.handleSignUp(res.data));
	}
	
	clickEvt(event){
		event.preventDefault();
		console.log(this.props.ErrorMsg);
		event.target.parentElement.style.display="none";
		document.getElementById("UserInput").value="";
		document.getElementById("PassInput").value="";
	};
	
render() {
		if(this.props.User.username!==undefined){
			return(
				<div style={{position:"absolute",top:0,left:0,right:0,bottom:0,width:"100%",height:"100%"}}>
					<nav className="navbar navbar-expand-lg navbar-dark bg-dark fixed-top" role="navigation">
						<div className="container">
							<a className="navbar-brand" href="#">CloudDrive</a>
							<button className="navbar-toggler border-0" type="button" data-toggle="collapse" data-target="#exCollapsingNavbar">
								&#9776;
							</button>
							<div className="collapse navbar-collapse" id="exCollapsingNavbar">
								<ul className="nav navbar-nav">
									<li className="nav-item"><a href="#" className="nav-link">About</a></li>
									<li className="nav-item"><a href="#" className="nav-link">Link</a></li>
									<li className="nav-item"><a href="#" className="nav-link">Service</a></li>
									<li className="nav-item"><a href="#" className="nav-link">More</a></li>
								</ul>
								<ul className="nav navbar-nav navbar-right flex-row justify-content-between ml-auto">
									<button type="button" id="dropdownMenu1" data-toggle="dropdown" class="btn btn-primary" style={{border:0, position:"relative",right:"-6600%", borderRadius:"0px", backgroundColor:"transparent"}} title="settings"><i className="fa fa-cog fa-fw fa-lg"></i> <span class="caret"></span></button>
										<ul className="dropdown-menu dropdown-menu-right mt-2">
											<li className="px-3 py-2">
												<form className="form">
													<div className="form-group">
														<input id="emailInput" placeholder="Email" class="form-control form-control-sm" type="text" required=""/>
													</div>
													<div className="form-group">
														<input id="passwordInput" placeholder="Password" class="form-control form-control-sm" type="text" required=""/>
													</div>
													<div className="form-group">
														<button type="submit" class="btn btn-primary btn-block">Login</button>
													</div>
													<div className="form-group text-center">
														<small><a href="#" data-toggle="modal" data-target="#modalPassword">Forgot password?</a></small>
													</div>
												</form>
											</li>
										</ul>
								</ul>
							</div>
						</div>
					</nav>
				</div>
			);
		}else{
			return(<Redirect to="/" />);
		}
	}
}

Profile.propTypes = {
	login: PropTypes.func.isRequired,
	User: PropTypes.object,
}

const mapStateToProps = (state) => ({
	User: state.Users.User,
	LoggedIn: state.Users.LoggedIn
})

export default withRouter(connect(mapStateToProps, {login})(Profile));
