import React, { Component } from 'react'
import ReactDOM from 'react-dom'
import { connect } from 'react-redux'
import { withRouter } from 'react-router-dom'
import { FilePond } from 'react-filepond';

class FileUploader extends Component {
    constructor(props) {
        super(props);

        this.handleFiles = this.handleFiles.bind(this);
        this.handleDrop = this.handleDrop.bind(this);
    }

    handleDrop(event) {
        ReactDOM.findDOMNode(this).submit();
    }

    handleFiles(event) {
        if (event.preventDefault) {
            event.preventDefault();
        }

    }

    render() {
        const FilePond = FilePond.create({
            name: 'Files'
        });

        FilePond = FilePond.setOptions({
            url: 'http://localhost:3001',
            timeout: 7000,
            process: {
                url: './backend/file/upload',
                method: 'POST',
                withCredentials: false,
            },
            revert: './revert',
            restore: './restore/',
            load: './load/',
            fetch: './fetch/'
        })
        return (
            <div style={{ width: "100%", paddingTop: "2.6%", height: "100%", paddingBottom: "100%" }}>
                <FilePond allowMultiple={true} server="http://localhost:3001/backend/file/upload" /*server="https://clouddriveserver.azurewebsites.net/backend/file"*/ />
            </div>
        )
    }
}

const mapStateToProps = (state) => ({
})

export default withRouter(connect(mapStateToProps, null)(FileUploader));
